(function(){
  $.ajaxSetup({
    contents: {
      script: false
    },
    error: function(xhr){
      if (this['error_' + xhr.status]) {
        this['error_' + xhr.status].apply(this, arguments);
        return;
      }
      cs.ui.notify(xhr.responseText
        ? JSON.parse(xhr.responseText).error_description
        : cs.Language.server_connection_error.toString(), 'warning', 5);
    }
  });
}).call(this);
